function reducer(state = 0, action) {
    console.log("reducer action: ", action.payload)
    console.log("reducer state: ", state)
    switch (action.type) {
        case 'UPDATE_DATA':
            return action.payload ;
        default:
            return state;
    }

}

export default reducer;
