import React from "react";
import { TextField, Button, Label, Dropdown } from "monday-ui-react-core"
import mondaySdk from "monday-sdk-js";
import { useState, useEffect } from "react";

const monday = mondaySdk();

const ListInput = ({nameHandler, countHandler, totalCount, dropdownOptions, dropdownHandler, clickFunction}) => {
    const [context, setContext] = useState();
    const [colOptions, setColOptions] = useState([])

    useEffect(() => {
        monday.listen("context", (res) => {
            // console.log("ListInput res: ", res)
            setContext(res.data);
        });

        console.log(context)

        if (!context) {
            return
        }

        console.log("Context: ", context)
            const boardId = context.boardId;

            const query = `query {
            boards(ids: ${boardId}) {
                columns {
                id
                title
                }
            }
            }`;
            monday.api(query).then((res) => {
                console.log("res: ", res);
                const columns = res.data.boards[0].columns;
                console.log("Columns: ", columns);
                const cols = columns.map(column => {
                    return {label: column.title, value: column.id}
                })
                console.log("cols: ", cols)
                setColOptions(cols)
            }).catch((err) => {
                console.log("Error fetching columns: ", err);
            });


    }, [])

    return (
        <div className="container">
            <div className="row">
                <div className="col">
                    <h4>Enter Batches</h4>
                </div>  
                <div className="col">
                    <div className="row">
                        {/* <div className="col">
                            <h4>Total</h4>
                        </div> */}
                        <div className="col">
                        <Label text={totalCount}></Label>
                        </div>
                    </div>


                </div>
                <div className="col">
                    <Dropdown placeholder="Target column" onChange={dropdownHandler} options={colOptions}></Dropdown>
                </div>
            </div>
            <div className="row pt-4">
                <div className="col">
                    <TextField onChange={nameHandler} type="text" placeholder="Batch name" />
                </div>
                <div className="col">
                    <TextField onChange={countHandler} type="number" placeholder="Batch count" />  
                </div>
                <div className="col">
                    <Button onClick={clickFunction} size={Button.sizes.SMALL} color={Button.colors.POSITIVE}>Add</Button>
                </div>
            </div>
        </div>
    );
}

export default ListInput
