import React from "react";
import { TextField, Button, Label, Dropdown, Divider, Heading, Text, Toggle } from "monday-ui-react-core"
import mondaySdk from "monday-sdk-js";
import { useState, useEffect, useRef } from "react";
import logo from '../assets/logo_cropped.png';
import { useLocation } from "react-router-dom";
import { useData } from '../modules/DataContext';

const monday = mondaySdk();
const storageInstance = monday.storage.instance;

const Onboard = () => {
    const [context, setContext] = useState();
    const [customBatchName, setCustomBatchName] = useState(false)
    const [totalBatches, setTotalBatches] = useState(0);
    // const {totalBatches} = useData()


    const handleCustomBatch = (evt) => {
        setCustomBatchName(evt) 
        storageInstance.setItem('customBatchName_'/* + context.itemId*/, JSON.stringify(evt));
        // localStorage.setItem('selectedOption_' + context.itemId, JSON.stringify(selectedOption));
        console.log("customBatchName: ", evt) 
      }

      useEffect(() => {
        console.log("Total Batches Change: ", totalBatches)
      }, [totalBatches])

      useEffect(() => {
        console.log("----App.js UseEffect #1----")
        // Notice this method notifies the monday platform that user gains a first value in an app.
        // Read more about it here: https://developer.monday.com/apps/docs/mondayexecute#value-created-for-user/
        monday.execute("valueCreatedForUser");

        monday.listen("context", (res) => {
          console.log("useEffect storage res: ", res)
          setContext(res.data);

          storageInstance.getItem('listItems_' + res.data.itemId).then(response => {
            // console.log("Item Response: ", response)
            setListItems(JSON.parse(response.data.value) || []);  
          });
          storageInstance.getItem('totalCount_' + res.data.itemId).then(response => {
            console.log("Count Response: ", response.data.value)
            const parsedCount = parseInt(response.data.value)
            setTotalCount(parsedCount || 0);
          });
          storageInstance.getItem('selectedOption_'/* + res.data.itemId*/).then(response => {
            console.log("Option Response: ", response)
            setSelectedOption(JSON.parse(response.data.value) || []);
          });
          storageInstance.getItem('printerOption_' + res.data.itemId).then(response => {
            console.log("Printer Response: ", response.data.value)
            setPrinterOptions(JSON.parse(response.data.value) || []);
          });
          storageInstance.getItem('totalBatches_').then(response => { 
            console.log("Total Batches Response res: ", response)
            console.log("Total Batches Response: ", response.data.value)
            setTotalBatches(JSON.parse(response.data.value));
          });


        });


      }, [/*listItems, colOptions*/]);



    console.log("Onboard totalBatches: ", totalBatches)
    return (
        <div className="container">
            <div className="row mb-2">
                <div className="col text-center"><img width="30%" src={logo} /></div>
            </div>
            <div className="row">
                <Divider></Divider>
            </div>
            <div className="row mb-2">
                <div className="col">
                    <div className="row">
                        <div className="col text-center">
                            <h4>Current Plan</h4>
                        </div>
                    </div>
                    <div className="row">
                        <div className="col text-center">
                            <Label text="Free" color="positive" kind="line"></Label>
                        </div>
                    </div>
                </div>
                <div className="col">
                    <div className="row">
                        <div className="col text-center">
                            <h4>Batches Created this Month</h4>
                        </div>
                    </div>
                    <div className="row">
                        <div className="col text-center">
                            <Label text={totalBatches} color="negative"></Label>
                        </div>
                    </div>
                </div>
                <div className="col">
                    <div className="row">
                        <div className="col text-center">
                            <h4>Batch Quota</h4>
                        </div>
                    </div>
                    <div className="row">
                        <div className="col text-center">
                            <Label text="100"></Label>
                        </div>
                    </div>
                </div>
            </div>
            <div className="row">
                <Divider></Divider>
            </div>
            {/* <div className="row my-3">
                <div className="col">
                    <h2 className="pb-3">Settings</h2>
                    <div className="row">
                        <div className="col-2">
                            <Toggle onChange={evt => handleCustomBatch(evt)}></Toggle>
                        </div>
                        <div className="col-4">
                            <h5 className="">Custom Batch Names</h5>
                        </div>

                    </div>

                </div>
            </div> */}
            <div className="row">
                <Divider></Divider>
            </div>
            <div className="row">
                <div className="col text-center">
                    <p><a href="#">Send us your feedback</a></p>
                </div>
                <div className="col text-center">
                    <p><a href="#">Documentation</a></p>
                </div>
                <div className="col text-center">
                    <p><a href="#">Email Support</a></p>
                </div>
            </div>
        </div>
    )
}

export default Onboard;
