import React from "react"
import { Checkbox, IconButton, Icon } from "monday-ui-react-core"
import { RecycleBin } from "monday-ui-react-core/icons" 
import { useState, useEffect } from "react";

const ListItem = ({itemName}) => {
    const [isChecked, setIsChecked] = useState(false);

    return (
        <div className="row align-middle">
            <div className="col-1">
                {isChecked ? <IconButton icon={RecycleBin} onCLick={() => setIsChecked(isChecked)}></IconButton> :  <span></span>}
            </div>
            <div className="col-1 align-bottom">
                <Checkbox className="align-middle" onChange={() => setIsChecked(!isChecked)} checked={isChecked} disabled={isChecked} label={itemName}></Checkbox>
            </div>

        </div>
    );
}

export default ListItem
