import React from "react"
import { Box } from "monday-ui-react-core"
import ListItem from "./ListItem.js"
import mondaySdk from "monday-sdk-js";
import { useState, useEffect } from "react";

const monday = mondaySdk();

const List = ({items}) => {
    const [context, setContext] = useState();


    const useEffect = () => {
        monday.listen("context", (res) => {
            // console.log("res: ", res)
            setContext(res.data);
        });

        console.log("Context: ", context)
        const boardId = context.boardId;

        const query = `query {
        boards(ids: ${boardId}) {
            columns {
            id
            title
            }
        }
        }`;
        monday.api(query).then((res) => {
            console.log("res: ", res);
            const columns = res.data.boards[0].columns;
            console.log("Columns: ", columns);
            const cols = columns.map(column => {
                return {label: column.title, value: column.id}
            })
            console.log("cols: ", cols)
            setColOptions(cols)
        }).catch((err) => {
            console.log("Error fetching columns: ", err);
        });
    }

    return (
        <div>
            <Box textColor={Box.textColors.PRIMARY_TEXT_COLOR} border={Box.borders.DEFAULT} rounded={Box.roundeds.MEDIUM}>
                {items.map((item) => (
                    item
                    ))
                }
            </Box>
        </div>
    );
}
// <ListItem key={item.key} itemName={item.itemName} itemCount={item.itemCount} handleDelete={item.handleDelete} handleTotalCount={item.changeTotalCount}></ListItem>
export default List
